function(){
  var params = {{cjs_nv_getParam}}, cContent, nRank, nKeyword;

  if (params.hasOwnProperty("n_rank")) nRank = params.n_rank;
  if (params.hasOwnProperty("n_keyword")) nKeyword = decodeURIComponent(params.n_keyword);
  if (nRank && nKeyword){
	cContent = nRank + '|' + nKeyword;
  } else if (nRank){
    cContent = nRank;
  } else {
    cContent = nKeyword;
  }
  return cContent;
}

