function() {
    var referrer_hostname = {{referrerHost}};
	var ref_url = {{Referrer}};
	if (referrer_hostname == "search.naver.com" || referrer_hostname =="m.search.naver.com"){
    	var pattern = new RegExp('[\\?&]oquery=([^&#]*)');
		var query = ref_url.match(pattern);
		var keyword = query[1].replace( /\+/gi, "%20");
		var result = decodeURI(keyword);
		return result;
    }
}

